SELECT _id, _display_name, date_modified, date_added, mime_type, duration, _size, width, height, _data, relative_path, datetaken, resolution FROM video WHERE ((((is_pending=0 AND NOT _modifier=4) OR (is_pending=1 AND lower(_data) NOT REGEXP '.*/\.pending-(\d+)-([^/]+)$' AND owner_package_name IN ( 'com.zhiliaoapp.musically' )))) AND (is_trashed=0) AND (volume_name IN ( 'external_primary' ))) ORDER BY _id DESC LIMIT 300 offset 0;
