SELECT _id, _display_name, date_modified, date_added, mime_type, _size, width, height, relative_path, _data, datetaken, orientation FROM images WHERE ((((is_pending=0 AND NOT _modifier=4) OR (is_pending=1 AND lower(_data) NOT REGEXP '.*/\.pending-(\d+)-([^/]+)$' AND owner_package_name IN ( 'com.zhiliaoapp.musically' )))) AND (is_trashed=0) AND (volume_name IN ( 'external_primary' ))) AND ((mime_type not like '%gif%')) ORDER BY date_modified DESC LIMIT 300 offset 0;
